import React, { Component } from 'react'

export default class Reservations extends Component {
    constructor(props) {
        super(props)
        this.state = {
            pet: this.props.pet,
            date:""

        }
    }

    updateDate(date){
        this.setState({
            ...this.state,
            date:date
        })
    }

    render() {
        console.log(this.state.pet)
        return (
            <div className="reservations">

                <form>
                    <h4>Pide cita para {this.state.pet.name}</h4>
                    <input type="hidden" name="pet" value={this.state.pet.name}></input>
                    <input type="hidden" name="email" value={this.props.user}></input>
                    <label for="start">Elige un d√≠a</label>
                    <input type="date" id="start" name="trip-start"
                        value={this.state.date}
                        min="2019-10-18" 
                        onChange={()=>this.updateDate(this.state.value)}>
                    </input>

                    <label for="appt">Choose a time for your meeting:</label>

                    <input type="time" id="appt" name="appt"
                        min="09:00" max="18:00" required></input>
                </form>  
            </div>
        )
    }
}
