import React, { Component } from 'react'
import './Consults.css'
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom"

 class Consults extends Component {

    constructor(props) {
        super(props)
        this.state = {
            pet: this.props.pet,
            thread:this.props.thread,
            show:false
    }
}

        componentWillReceiveProps(nextProps) {
            this.setState({ ...this.state, pet: nextProps.pet })
        }

        setThread(thread){
            this.setState({ ...this.state, thread: thread,show:true }, () => console.log(this.state.show))
        }

    render() {
        return (
            <div className="consults">
                <Link to="/home"><i className="fa fa-arrow-left" aria-hidden="true"></i></Link>
                <h4>Hilos de consultas</h4>
                <div className="container">
                    {this.props.pet.thread.map(thread => <div onClick={()=>this.setThread(thread)} className="hilo">{thread.title}</div>)}
                </div>
        </div>
        )
    }
}
export default withRouter(Consults);
