import React, { Component } from 'react'
import './thread.css'
import Chart from '../Chart'
import { Link } from "react-router-dom";


export default class Thread extends Component {

    constructor(props){
        super(props)
        this.state={
            pet:this.props.pet,
            show:false,
            age:"",
            race:"",
            sex:"",
            castrated:"",
        }
    }

    componentWillReceiveProps(nextProps) {
        this.setState({ ...this.state, pet: nextProps.pet })
    }

    showForm(){
        this.setState({
            ...this.state,show:!this.state.show,
            age:this.props.pet.data.age ,
            race:this.props.pet.data.race,
            sex:this.props.pet.data.sex,
            castrated:this.props.pet.data.castrated,
        })
    }

    changeValue(){

    }




    render() {
        console.log(this.props.role)
        if(!!this.state.pet.name){
            if (this.props.role=="USER"){
            return (
                <div className="thread">
                    <h4>{this.props.pet.name}</h4>
                    <h5>{this.props.pet.type}</h5>
                    <div className="charts">
                        <Chart weight={this.props.pet.weight} />
                        <div className="generals">
                            <div>DATA</div>
                            <div>Age: {this.props.pet.data.age}</div>
                            <div>Race: {this.props.pet.data.race}</div>
                            <div>Sex: {this.props.pet.data.sex}</div>
                            <div>Castrated: {this.props.pet.data.castrated}</div>

                        </div>
                    </div>
                    <div className="options">
                        <Link to="/consults">
                            <div>Historial de consultas</div>
                        </Link>
                        <Link to="/reservations">
                            <div>Reserva una consulta</div>
                        </Link>
                        <Link to="/calendar">
                            <div>Calendario</div>
                        </Link>
                    </div>
                </div>

        )
            }else{
                return (
                    <div className="thread">
                        <h4>{this.props.pet.name}</h4>
                        <h5>{this.props.pet.type}</h5>
                        <div className="charts">
                            <Chart weight={this.props.pet.weight} />
                            {!this.state.show &&
                                <div className="generals">
                                    <div>DATA <i onClick={() => this.showForm()} class="fa fa-pencil" aria-hidden="true"></i></div>
                                    <div>Age: {}</div>
                                    <div>Race: {}</div>
                                    <div>Sex: {}</div>
                                    <div>Castrated: {}</div>
                                </div>
                            }

                            {this.state.show &&
                                <form className="generals">
                                    <div>DATA <i onClick={() => this.showForm()} class="fa fa-pencil" aria-hidden="true"></i></div>
                                    <input type="text" onChange={()=>{this.changeValue()}} value={this.state.age}></input>
                                    <input type="text" onChange={()=>{this.changeValue()}} value={this.state.race}></input>
                                    <input type="text" onChange={()=>{this.changeValue()}} value={this.state.sex}></input>
                                    <input type="text" onChange={()=>{this.changeValue()}} value={this.state.castrated}></input>
                                    <input type="submit" value="Editar"></input>
                                </form>
                            }

                        </div>
                        <div className="options">
                            <Link to="/consults">
                                <div>Historial de consultas</div>
                            </Link>

                            {/* <Link to="/calendar">
                                <div>Calendario</div>
                            </Link> */}
                        </div>
                    </div>

                )
            }
            }else{
                return(
                    <div className="thread">

                    <div className="nopet">Select a pet</div>
                    </div>
                )
            }
    }
}
